      //     </Accordion.Title>
      //     <Accordion.Content active={activeIndex === 1}>
      //       <List>{userPosts && this.displayTopPosters(userPosts)}</List>
      //     </Accordion.Content>

      //     <Accordion.Title
      //       active={activeIndex === 2}
